(function ($, window, document, undefined) {
  "use strict";

    $(document).ready(() => {
    function toggleSidebar() {
        $(".menu").on("click", function(){

        $("nav.sidebar").addClass("display");
      })
      $(".close-nav").on("click", function() {
        $("nav.sidebar").removeClass("display");
      });
    }

  
  });
})(jQuery, window, document);
